import React from "react";
import placeholderImg from "../assets/placeholder-product.jpg";

const products = [
  {
    id: 1,
    name: "Laptop",
    price: 79999,
    image: placeholderImg,
    details: "15.6 inch, 8GB RAM",
  },
  {
    id: 2,
    name: "Mobile Phone",
    price: 24999,
    image: placeholderImg,
    details: "128GB, Black",
  },
];

function formatINR(price) {
  return new Intl.NumberFormat("en-IN", {
    style: "currency",
    currency: "INR",
  }).format(price);
}

function Home() {
  return (
    <div style={{ padding: 30 }}>
      <h2>ShipWave Products</h2>
      <div style={{ display: "flex", gap: 28, flexWrap: "wrap" }}>
        {products.map((product) => (
          <div
            key={product.id}
            style={{
              border: "1px solid #eee",
              borderRadius: 8,
              padding: 16,
              width: 220,
              background: "#fff",
            }}
          >
            <img
              src={product.image}
              alt={product.name}
              style={{ width: "100%", height: 120, objectFit: "contain" }}
            />
            <h3>{product.name}</h3>
            <p>{product.details}</p>
            <p style={{ fontWeight: "bold" }}>{formatINR(product.price)}</p>
            <button
              style={{
                background: "#ff9900",
                color: "#fff",
                border: "none",
                padding: "8px 14px",
                borderRadius: 4,
                cursor: "pointer",
              }}
              onClick={() => alert("Add to cart functionality to be implemented")}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
